settings.custom_liquid:
{%- if product.tags contains "wholesale" -%}
{%- if  collection.previous_product or collection.next_product -%}
<div class="page-width prev_next">
<div class="libnav prev_product">{%- if  collection.previous_product  -%}
<a href={{ collection.previous_product }}><img src="{{ collection.previous_product.featured_image  | img_url: '100%', crop:center }}" alt="{{ collection.previous_product.title }}"><div>&larr; {{ collection.previous_product.title }}</div></a>{%- endif -%}</div>
<div class="libnav product_collection"><a href={{ collection.url }}><img src="{{ collection.featured_image  | img_url: '100%', crop:center }}" alt="{{ collection.title }}"><div>See all {{ collection.title }}</div></a></div>
<div class="libnav next_product">{%- if  collection.next_product  -%}<a href={{ collection.next_product }}><img src="{{ collection.next_product.featured_image  | img_url: '100%', crop:center }}" alt="{{ collection.next_product.title }}"><div>{{ collection.next_product.title }} &rarr;</div></a>{%- endif -%}</div>
</div>
{%- endif -%}
{%- endif -%}

{% if collection.title %}
{% if collection.previous_product %}
{% assign productPrev = collection.previous_product %}
{% else %}
{% assign productPrev = collection.products | last %}
{% endif %}
{% if collection.next_product %}
{% assign productNext = collection.next_product %}
{% else %}
{% assign productNext = collection.products | first %}
{% endif %}

{% render 'library-nav',
prev_product: productPrev,prev_product_url:productPrevURL,next_product: productNext
product: product
%}

{% endif %}
